mapper.insert:
   INSERT
     INTO MESSAGE
        (
          MSG_NO
        , MSG_CONTENT
        , SEND_DATE
        , CH_NO
        , SENDER_NO
        , RECEIVER_NO
        , MSG_STATUS
	     )
   VALUES
        (
          SEQ_MESSAGE_NO.NEXTVAL
        , #{msgContent}
        , SYSDATE
        , #{chNo}
        , #{senderNo}
        , #{receiverNo}
        , #{msgStatus}
   )

mapper.update:
update message
   set msg_status = #{status}
 where msg_no = #{msgNo}
