mapper.insert:
		INSERT 
		  INTO member 
		     ( user_no
		     , user_id
		     , user_pwd
		     , user_name
		     , email
		     , phone
		     , gender
		     , age
		     , address
		     , user_level
		     , profile_pic
		     , qr
		     )
        VALUES
        	 (
        	   seq_mno.nextval
        	 , #{userId}
        	 , #{userPwd}
        	 , #{userName}
        	 , #{email}
        	 , #{phone}
        	 , #{gender}
        	 , #{age}
        	 , #{address}
        	 , #{userLevel} 
        	 , #{profilePic}
        	 , #{qr}
        	 )

INSERT 
  INTO MEMBER_INFO
     (
       user_no
     , height
     , weight
     , disease
     , goal
     )
VALUES
     (
       #{userNo}
     , #{height}
     , #{weight}
     , #{disease}
     , #{goal}
     )

insert 
  into schedule
     (
       schedule_no
     , user_no
     , schedule_title
     , start_date
     , end_date
     , back_color
     )
values
     (
       seq_cno.nextval
     , #{userNo}
     , #{scheduleTitle}
     , #{startDate}
     , #{endDate}
     , #{backColor}
     )

mapper.select:
select count(schedule_no)
  from schedule
 where schedule_title = #{scheduleTitle}
   and start_date = #{startDate}

