	 )

mapper.update:
update qrinfo
   set attendance = #{attendance}


		     , att_status = '결석'


   			, att_status = '출근'


  where qr_no = #{qrNo}

update member_info
   set height = #{height}
     , weight = #{weight}
     , disease = #{disease, typeHandler=com.kh.fitguardians.common.StringArrayTypeHandler}
     , goal = #{goal}
 where user_no = #{userNo}

update member
   set user_pwd = #{userPwd}
 where user_no = #{userNo}

update member
   set email = #{email}
 where user_no = #{userNo}

