mapper.insert:
		INSERT 
		  INTO member 
		     ( user_no
		     , user_id
		     , user_pwd
		     , user_name
		     , email
		     , phone
		     , gender
		     , age
		     , address
		     , user_level
		     , profile_pic
		     , qr
		     )
        VALUES
        	 (
        	   seq_mno.nextval
        	 , #{userId}
        	 , #{userPwd}
        	 , #{userName}
        	 , #{email}
        	 , #{phone}
        	 , #{gender}
        	 , #{age}
        	 , #{address}
        	 , #{userLevel} 
        	 , #{profilePic}
        	 , #{qr}
        	 )

INSERT 
  INTO MEMBER_INFO
     (
       user_no
     , height
     , weight
     , disease
     , goal
     )
VALUES
     (
       #{userNo}
     , #{height}
     , #{weight}
     , #{disease}
     , #{goal}
     )

insert 
  into schedule
     (
       schedule_no
     , user_no
     , schedule_title
     , start_date
     , end_date
     , back_color
     )
values
     (
       seq_cno.nextval
     , #{userNo}
     , #{scheduleTitle}
     , #{startDate}
     , #{endDate}
     , #{backColor}
     )

mapper.select:
select count(schedule_no)
  from schedule
 where schedule_title = #{scheduleTitle}
   and start_date = #{startDate}

mapper.insert:
insert 
  into qrinfo
     (
       qr_no
     , id
     , type
     , created_at
     , valid_until
     )
values
	 (
	   seq_qno.nextval
	 , #{id}
	 , #{type}
	 , #{createdAt}
	 , #{validUntil}
	 )

mapper.update:
update qrinfo
   set attendance = #{attendance}


		     , att_status = '결석'


   			, att_status = '출근'


  where qr_no = #{qrNo}

mapper.select:
select user_no
     , height
     , weight
     , disease
     , goal
  from member_info
where user_no = #{userNo}

select user_no
     , height
     , weight
     , disease
     , goal
  from member_info
where user_no = #{userNo}



update member_info
   set height = #{height}
     , weight = #{weight}
     , disease = #{disease, typeHandler=com.kh.fitguardians.common.StringArrayTypeHandler}
     , goal = #{goal}
 where user_no = #{userNo}



update member
   set user_pwd = #{userPwd}
 where user_no = #{userNo}



update member
   set email = #{email}
 where user_no = #{userNo}



update member
   set status = 'N'
 where user_no = #{userNo}
